import { defineComponent, reactive } from "vue";
import { User } from "@/types/User";
import { judgeDelete } from "@/composables/judgValue";
import { useRouter } from "vue-router";

/* ******************************************************************************************
型定義
****************************************************************************************** */
//検索値格納用
interface SearchValue {
  userIdOrEmail: string;
}

//状態格納用
interface State {
  searchValue: SearchValue;
  users: Array<User>;
  startIndex: number;
  endIndex: number;
}

export default defineComponent({
  setup() {
    /* ******************************************************************************************
    モジュール全体で使用する変数の定義
    ****************************************************************************************** */
    const router = useRouter();
    const is_delete = judgeDelete;

    //検索値格納用の変数を初期化
    const searchValueInit: SearchValue = {
      userIdOrEmail: "",
    };

    //状態をリアクティブで定義
    const refState = reactive<State>({
      searchValue: searchValueInit,
      users: [],
      startIndex: 1,
      endIndex: 51,
    });

    /* ******************************************************************************************
    モジュール全体で使用する変数の定義
    ****************************************************************************************** */
    const getUserList = () => {
      //urlを設定
      let url = new URL("http://localhost:3000/users");
      url.searchParams.set(
        "user_id_or_email",
        refState.searchValue.userIdOrEmail
      );

      //リクエストを送信
      const oReq = new XMLHttpRequest();
      oReq.open("GET", url.toString(), false);
      oReq.send();
      return JSON.parse(oReq.response);
    };

    const setUsers = () => {
      refState.users = getUserList();
    };

    setUsers();

    /* ******************************************************************************************
    モジュール全体で使用する変数の定義
    ****************************************************************************************** */
    const transitionUserEdit = (id: number): void => {
      router.push({
        name: "UserEdit",
        params: {
          id: id,
        },
      });
    };

    return {
      refState,
      is_delete,
      setUsers,
      transitionUserEdit,
    };
  },
});
